import { Component } from '@angular/core';
import { AuthService } from './auth.service';
import { Router } from '@angular/router';
import { Subject } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'hospital-app';
  userId: string | null = null;
  private userIdSubject: Subject<string | null> = new Subject<string | null>();
  role: string | any;
  private _isLoggedIn: boolean = false;

  constructor(private authService: AuthService, private router: Router) {}

  ngOnInit() {
    if (this.authService.isLoggedIn()) {
      this.userId = this.authService.getUserId();
      this.role = this.authService.getUserRole(); // Retrieve the user's role from the service
      console.log(this.role)
    }

    // this.authService.subscribeToUserIdChanges().subscribe(
    //   (userId: string | null) => {
    //     this.userId = userId;
    //     this.role = this.authService.getUserRole(); // Update the user's role when it changes

    //   },
    //   (error: any) => {
    //     console.error('Error fetching user ID:', error);
    //   }
    // );
  }

  isDoctorLoggedIn(): boolean {
    const role = this.authService.getRole();
    return role === 'doctor';
  }

  isPatientLoggedIn(): boolean {
    const role = this.authService.getRole();
    return role === 'patient';
  }

  isLoggedIn(): boolean {
    const token = localStorage.getItem('token');
    return !!token;
  }



  // isLoggedIn(): boolean {
  //   return this.authService.isLoggedIn();
  // }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8">
  <title>HospitalApp</title>
  <base href="/">
  <meta name="viewport" content="width=device-width, initial-scale=1">
  <link rel="icon" type="image/x-icon" href="favicon.ico">

  <!-- Bootstrap CSS -->
  <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@5.7.0/dist/css/bootstrap.min.css">
</head>
<body>


  <nav class="navbar navbar-expand-lg navbar-light bg-light">
    <div class="container">
      <a class="navbar-brand" routerLink="/">HospitalApp</a>
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNav">
        <ul class="navbar-nav">
          <li class="nav-item">
            <a class="nav-link" href="/">Home</a>
          </li>
          <li class="nav-item">
            <a class="nav-link" href="/about">About</a>
          </li>
          <li class="nav-item">
            <a class="nav-link" href="/contact">Contact</a>
          </li>
          <li *ngIf="isLoggedIn() && isDoctorLoggedIn()" class="nav-item">
            <a routerLink="/doctordashboard" class="nav-link" >Doctor Dashboard</a>
          </li>

          <li *ngIf="isLoggedIn() && isPatientLoggedIn()" class="nav-item">
            <a routerLink="/patientdashboard" class="nav-link" >Patient Dashboard</a>
          </li>


          <li class="nav-item" *ngIf="!isLoggedIn()">
            <a class="nav-link" routerLink="/register">Register</a>
          </li>
          <li class="nav-item" *ngIf="!isLoggedIn()">
            <a class="nav-link" routerLink="/login">Login</a>
          </li>
          <li class="nav-item" *ngIf="isLoggedIn()">
            <a class="nav-link" (click)="logout()">Logout</a>
          </li>


        </ul>
      </div>
    </div>
  </nav>

  <div class="container">
    <router-outlet></router-outlet>
  </div>

  <footer class="bg-dark text-light p-2 fixed-bottom">
    <div class="container text-center">
        <p>&copy; 2023 Hospital Management App. All rights reserved.</p>
    </div>
</footer>
  <!-- Bootstrap JS Bundle (Popper.js included) -->
  <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.7.0/dist/js/bootstrap.bundle.min.js"></script>
</body>
</html>
